// SPDX-License-Identifier: MIT
// pragma solidity >=0.4.22 <0.9.0;
pragma solidity ^0.6.0;
import "@chainlink/contracts/src/v0.6/ChainlinkClient.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * 该文件是一些共用的方法或字段
 *
 *
 */

/**
 *   该抽象合约提供使用控制的方法
 *   创建资产合约时，需要提供 IPFS 文件的 hash 值，有两种创建办法，
 *   一个是完全线上处理，1)请求 DAM 合约创建资产；2）请求预言机获取可验证证明 VC 然后构造 DID 档案上传到 IPFS，返回。
 *   另一种是线下操作，这里采用这种节省 GAS。但是创建 VC 时需要绑定合约的地址，所以需要先生成合约，然后获取 VC，在上传到 IPFS，
 *   然后将获取的文件 hash 值更新到合约中
 *  所以当还完成更新文件 hash 值到合约中时，合约是不可用的，需要控制符控制。
 */
abstract contract Base is Ownable {
    event RecieveEth(address addr, uint256 amount);
    // 注意与 Asset 状态转移的不同
    enum State {
        Frozen,
        Normal
    }
    event StateChangeEvent(State from, State to);
    event LogEvent(string functionName, int8 identity);
    State public state;

    constructor() public {
        state = State.Normal;
    }

    modifier hasState(State _state) {
        require(_state == State.Frozen, "not match expected state");
        _;
    }

    function changeState(State _state) public {
        State old = state;
        state = _state;
        emit StateChangeEvent(old, state);
    }

    function freeze() public onlyOwner hasState(State.Normal) {
        changeState(State.Frozen);
    }

    function deFreeze() public onlyOwner hasState(State.Frozen) {
        changeState(State.Normal);
    }
}

/**
 *  使用 chainlink 服务需要指定发行 link 的合约，设置0x0 时表示公网环境
 *
 *
 */
abstract contract LinkBase {
    address public link;

    function setLink(address _link) public {
        link = _link;
    }
}

abstract contract DamBase {
    address public damAddress;

    function setDamAddress(address _addr) public {
        damAddress = _addr;
    }
}

abstract contract ChainlinkBase is ChainlinkClient, Ownable {
    event CallbackInvokeEvent(string callbackName, bool data);
    event CallbackInvokeEvent(string callbackName, uint256 data);

    function initLinkAddress(address _link) public {
        if (_link == address(0)) {
            setPublicChainlinkToken();
        } else {
            setChainlinkToken(_link);
        }
    }

    /**
     * @notice Returns the address of the LINK token
     * @dev This is the public implementation for chainlinkTokenAddress, which is
     * an internal method of the ChainlinkClient contract
     */
    function getChainlinkToken() public view returns (address) {
        return chainlinkTokenAddress();
    }

    /**
     * @notice Creates a request to the specified Oracle contract address
     * @dev This function ignores the stored Oracle contract address and
     * will instead send the request to the address specified
     * @param _oracle The Oracle contract address to send the request to
     * @param _jobId The bytes32 JobID to be executed
     * @param _url The URL to fetch data from
     * @param _path The dot-delimited path to parse of the response
     */
    function createRequestTo(
        address _oracle,
        bytes32 _jobId,
        uint256 _payment,
        bytes4 _callback,
        string memory _url,
        string memory _path
    ) public returns (bytes32 requestId) {
        Chainlink.Request memory req = buildChainlinkRequest(
            _jobId,
            address(this),
            _callback
        );
        req.add("url", _url);
        req.add("path", _path);
        requestId = sendChainlinkRequestTo(_oracle, req, _payment);
    }

    /**
     * @notice Creates a request to the specified Oracle contract address
     * @dev This function ignores the stored Oracle contract address and
     * will instead send the request to the address specified
     * @param _oracle The Oracle contract address to send the request to
     * @param _jobId The bytes32 JobID to be executed
     * @param _url The URL to fetch data from
     * @param _path The dot-delimited path to parse of the response
     * @param _times The number to multiply the result by
     */

    function createRequestTo(
        address _oracle,
        bytes32 _jobId,
        uint256 _payment,
        bytes4 _callback,
        string memory _url,
        string memory _path,
        int256 _times
    ) public returns (bytes32 requestId) {
        Chainlink.Request memory req = buildChainlinkRequest(
            _jobId,
            address(this),
            _callback
        );
        req.add("url", _url);
        req.add("path", _path);
        req.addInt("times", _times);
        requestId = sendChainlinkRequestTo(_oracle, req, _payment);
    }

    /**
     * @notice Call this method if no response is received within 5 minutes
     * @param _requestId The ID that was generated for the request to cancel
     * @param _payment The payment specified for the request to cancel
     * @param _callbackFunctionId The bytes4 callback function ID specified for
     * the request to cancel
     * @param _expiration The expiration generated for the request to cancel
     */
    function cancelRequest(
        bytes32 _requestId,
        uint256 _payment,
        bytes4 _callbackFunctionId,
        uint256 _expiration
    ) public onlyOwner {
        cancelChainlinkRequest(
            _requestId,
            _payment,
            _callbackFunctionId,
            _expiration
        );
    }

    /**
     * @notice Allows the owner to withdraw any LINK balance on the contract
     */
    function withdrawLink() public onlyOwner {
        LinkTokenInterface link = LinkTokenInterface(chainlinkTokenAddress());
        require(
            link.transfer(msg.sender, link.balanceOf(address(this))),
            "Unable to transfer"
        );
    }
}

